jQuery(function ($) { 
    
    /**
     *
     * Object Fit Polyfill for images
     *
     */
    
    objectFitImages() 
    
    /**
    *
    * Sticky Header
    *
    **/

    $(window).on("scroll", function() {
        if($(window).scrollTop() > 50) {
            $("body").addClass("sticky-header-active");
        } else {
           $("body").removeClass("sticky-header-active");
        }
    });

    $(window).on("scroll", function() {
        if($(window).scrollTop() > 50) {
            $(".site-header-main, .site-header-mobile").addClass("sticky");
        } else {
           $(".site-header-main, .site-header-mobile").removeClass("sticky");
        }
    });


    /**
     *
     * WooCommerce Sidebar
     *
     */

     $(".woocommerce-shop-content__sidebarContent").stick_in_parent({
         offset_top: 57,
     });

});